package javabasics;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x; //declaring variable assigning
		x=34; //assigning value to variable
		System.out.println(x);
		x=23;
		System.out.println(x+5); 	// int supports + - / * %
		
		String words;
		//words = 20; //type mismatch - compile time error
		words = "this is a sentence";
		System.out.println(words + " and more words");	//String only supports + operator and it appends two strings
		
		String newStr = "new String";	//combining declaration and assignment
		
		
/**	LESSONS LEARNT
1. Code is executed line by line
2. We have to declare the variables with a datatype
3. We can change the data that goes into a variable

**/
	}

}
